body.script:
        // Функция для получения параметров из URL
        function getQueryParameter(name) {
            const urlParams = new URLSearchParams(window.location.search);
            return urlParams.get(name);
        }

        // Получаем postId из URL
        const postId = getQueryParameter('id');

        if (postId) {
            // Здесь вы можете сделать запрос к вашему API для получения данных поста
            fetch(`https://yourapi.com/posts?id=${postId}`)
                .then(response => response.json())
                .then(data => {
                    const postDetails = document.getElementById('post-details');
                    postDetails.innerHTML = `

${data.title}

${data.content}

                    `;
                })
                .catch(error => {
                    console.error('Ошибка при получении поста:', error);
                });
        } else {
            document.getElementById('post-details').innerText = 'Пост не найден.';
        }
